import React, {useState} from 'react'

export default function TextForm(props) {
    const [text, setText] = useState("Enter text here");
    
    const handleUpClick =()=>{
        let newText = text.toUpperCase();
        setText(newText)
    }

    const handleLoClick =()=>{
      let newText = text.toLowerCase();
      setText(newText)
  }

  const handleClClick =()=>{
    let newText = "";
    setText(newText)
}

const handleScClick = () => {
  const currentText = text;
  const sentences = currentText.split(/(?<=[.!?])\s+/);
  const newText = sentences.map(sentence => {
    return sentence.charAt(0).toUpperCase() + sentence.slice(1);
  }).join(' ');
  setText(newText);
};

const handleCcClick = (text) => {
  const words = text.split(" ");
  const capitalizedWords = words.map(word => {
    return word.charAt(0).toUpperCase() + word.slice(1);
  });
  const capitalizedText = capitalizedWords.join(' ');
  return capitalizedText;
};



    const handleOnChange = (event)=>{
        setText(event.target.value)
    }

  return (
    <>
    <div>
    <h1 style={{ textAlign: 'left' }}>{props.heading}</h1>
<div className="mb-3">   
  <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
</div>
<button className="btn btn-primary mx-1" onClick={handleScClick}>Convert to sentence case</button>
<button className="btn btn-primary mx-1" onClick={handleUpClick}>Convert to uppercase</button>
<button className="btn btn-primary mx-1" onClick={handleLoClick}>Convert to lowercase</button>
<button className="btn btn-primary mx-1" onClick={handleCcClick}>Convert to capitalized case</button>
<button className="btn btn-primary mx-1" onClick={handleClClick}>Clear</button>
    </div>
    <div className="container my-3">
      <h1>Your text summary</h1>
      <p>{text.trim().split(" ").filter(word => word !== '').length} words and {text.length}</p>
      <p>{0.008 * text.split(" ").length} Minutes read</p>
      <h2>Preview</h2>
      <p>{text}</p>
    </div>
    </>
  )
}
