import React, {useState} from 'react'

export default function TextForm(props) {
    const [text, setText] = useState("Enter text here");
    
    const handleUpClick =()=>{
        console.log("uppercase was clicked" + text);
        let newText = text.toUpperCase();
        setText(newText)
    }

    const handleOnChange = (event)=>{
        console.log("On change");
        setText(event.target.value)
    }

  return (
    <div>
    <h1 style={{ textAlign: 'left' }}>{props.heading}</h1>
<div className="mb-3">   
  <textarea className="form-control" value={text} onChnage={handleOnChange} id="myBox" rows="8"></textarea>
</div>
<button className="btn btn-primary" onClick={handleUpClick}>Conver to uppercase</button>
    </div>
  )
}
